import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Header from '../components/Header';
// import { Link } from 'react-router-dom';
import fetchCurrency from '../services/Api';
import { actionWallet, actionFetchCurrencies } from '../actions';

class Wallet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      currenciesLocal: [],
    };
  }

  componentDidMount = async () => {
    this.getCurrencyFromApi();
  }

  getCurrencyFromApi = async () => {
    this.setState({ isLoading: true });
    const { currenciesGlobal } = this.props;
    const { currenciesLocal } = this.state;
    const moedas = await fetchCurrency();
    this.setState({ currenciesLocal: moedas });
    await currenciesGlobal(currenciesLocal);
    this.setState({ isLoading: false });
  }

  render() {
    const { isLoading } = this.state;
    return (
      <div>
        <Header />
        { isLoading ? <p>Loading</p>
          : (
            <button
            // onClick={ }
              type="button"
            >
              click
            </button>
          )}
      </div>
    );
  }
}

Wallet.propTypes = {
  currenciesGlobal: PropTypes.func,
}.isRequired;

const mapDispatchToProps = (dispatch) => ({
  currenciesGlobal: () => dispatch(actionWallet(state)),
});

const mapStateToProps = (state) => ({
  wallet: state.wallet,
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
