// Esse reducer será responsável por tratar as informações da pessoa usuária
import { USER } from '../actions';

const initialState = {
  user: {
    email: '',
  },

};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
  case USER:
    return {
      ...state,
      email: action.email,
    };
  default:
    return {
      ...state,
    };
  }
};

export default userReducer;
