import React from 'react';
// import { connect } from 'react-redux';
import '../css/login.css';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isDisabled: true,
      textEmail: '',
      senha: '',
    };
  }

  componentDidMount = () => {
    this.verifyButton();
  }

  handleForm = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  };

  verifyButton = () => {
    const { senha, textEmail } = this.state;
    const six = 6;
    if (senha.length >= six) this.setState({ isDisabled: false });
  }

  handleClick = () => {
    console.log('eu sou d+ ')
  }

  render() {
    const { isDisabled, textEmail, senha } = this.state;
    return (
      <div>
        <form className="userContainer">
          <h3>Login</h3>
          <li>
            <input
              name="textEmail"
              type="email"
              placeholder="Email"
              data-testid="email-input"
              onChange={ this.handleForm }
              value={ textEmail }
            />
          </li>
          <li>
            <input
              name="senha"
              type="password"
              placeholder="Senha"
              onChange={ this.handleForm }
              data-testid="password-input"
              value={ senha }
            />
          </li>
          <button
            type="submit"
            disabled={ isDisabled }
            onClick={ this.handleClick }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  userEmail: state.reducer.userEmail,
});


export default Login;
