// Coloque aqui suas actions
import fetchCurrency from '../services/Api';

export const USER = 'USER';
export const WALLET = 'WALLET';
export const EXPENSE = 'EXPENSE';

export const actionUser = (email) => ({
  type: USER,
  email,
});

export const actionWallet = (currencies) => ({
  type: WALLET,
  currencies,
});

export const actionExpense = (expense) => ({
  type: EXPENSE,
  expense,
});

// Essa função faz a chamada da função da API, faz o thrunk e prepara a dispatch chamando a action
export const actionFetchCurrencies = () => async (dispatch) => {
  const returnJson = await fetchCurrency();
  const currenciesGlobal = Object.keys(returnJson).filter((item) => item !== 'USDT');
  dispatch(actionWallet(currenciesGlobal));
  dispatch()
};
