import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { actionExpense } from '../actions';
import fetchCurrency from '../services/Api';

class FormExpenses extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      valor: '',
      moeda: '',
      metodo: '',
      despesa: '',
      descricao: '',
      data: '',
      id: 0,
    };
  }

  handleForm = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  refreshRates = async () => {
    const rates = await fetchCurrency();
    this.setState({ rates });
  }

  saveInfo = () => {
    const { walletExpenses } = this.props;
    this.setState((prev) => ({
      id: prev.id + 1,
    }));

    walletExpenses(this.state);
    this.setState({
      valor: '',
      moeda: '',
      metodo: '',
      despesa: '',
      descricao: '',
      data: '',
    });
  }

  render() {
    const { currencies } = this.props;
    const { moeda, descricao, valor, metodo, despesa, data } = this.state;
    const arrayTipos = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    return (
      <form className="formExpensesContainer">
        <input
          type="date"
          name="data"
          onChange={ this.handleForm }
          value={ data }
          placeholder="Data"
        />
        <input
          type="number"
          name="valor"
          data-testid="value-input"
          onChange={ this.handleForm }
          value={ valor }
          placeholder="Valor da despesa"
        />
        <input
          type="text"
          name="descricao"
          data-testid="description-input"
          onChange={ this.handleForm }
          value={ descricao }
          placeholder="Descrição da despesa"
        />
        <select
          type="text"
          name="metodo"
          data-testid="method-input"
          onChange={ this.handleForm }
          value={ metodo }
          placeholder="Método de Pagamento"
        >
          <option>Escolha a forma de pagamento</option>
          <option value="Dinheiro">Dinheiro</option>
          <option value="Cartão de crédito">Cartão de crédito</option>
          <option value="Cartão de débito">Cartão de débito</option>
        </select>
        <select
          type="text"
          name="despesa"
          onChange={ this.handleForm }
          value={ despesa }
          placeholder="Tipo de Despesa"
          data-testid="tag-input"
        >
          <option>Escolha o tipo de despesa</option>
          {
            arrayTipos.map((tipo, index) => (
              <option key={ index } value={ tipo }>{tipo}</option>
            ))
          }
        </select>
        <label htmlFor="coin-select">
          Moeda:
          <select
            id="coin-select"
            name="moeda"
            value={ moeda }
            onChange={ this.handleForm }
            label="Moeda"
          >
            <option>Escolha a Moeda</option>
            {
              currencies.map((item, index) => (
                <option key={ index } value={ item }>{item}</option>
              ))
            }
          </select>
        </label>
        <button
          type="button"
          onClick={ this.saveInfo }
        >
          Adicionar despesa
        </button>
      </form>
    );
  }
}

FormExpenses.propTypes = {
  currencies: PropTypes.string,
}.isRequired;

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  walletExpenses: (state) => dispatch(actionExpense(state)),
});

export default connect(mapStateToProps, mapDispatchToProps)(FormExpenses);
