import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { actionExpense, actionFetchCurrencies, actionIsEdit,
  actionDeleteExpense } from '../actions';
import fetchCurrency from '../services/Api';

class EditExpen extends React.Component {
  constructor(props) {
    super(props);
    const { idEstado: { id, value, description, currency, method, tag } } = this.props;
    this.state = {
      id,
      value,
      description,
      currency,
      method,
      tag,
      // data: '',
    };
  }

  componentDidMount = async () => {
    await this.refreshRates();
  }

  handleForm = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  refreshRates = async () => {
    const rates = await fetchCurrency();
    this.setState({ exchangeRates: rates });
  }

  saveEditInfo = () => {
    this.refreshRates();
    const { walletExpenses, deleteExpense, wallet, isEdit } = this.props;
    const expenseDeleted = wallet.expenses.filter((item) => item !== wallet.id);
    deleteExpense(expenseDeleted);
    walletExpenses(this.state);
    this.setState({
      id: lastId,
      value: 0,
      currency: 'USD',
      method: '',
      tag: '',
      description: '',
    });
    isEdit(false);
  }

  changeValue = (value) => value.toFixed(2);

  render() {
    const { currencies } = this.props;
    const { currency, description, value, method, tag } = this.state;
    const arrayTipos = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    return (
      <form className="formExpensesContainer">
        <input
          type="currency"
          name="value"
          data-testid="value-input"
          onChange={ this.handleForm }
          value={ value }
          placeholder="Valor da despesa"
          min="0.00"
          max="10000000.00"
          step="0.01"
        />
        <input
          type="text"
          name="description"
          data-testid="description-input"
          onChange={ this.handleForm }
          value={ description }
          placeholder="Descrição da despesa"
        />
        <select
          type="text"
          name="method"
          data-testid="method-input"
          onChange={ this.handleForm }
          value={ method }
          placeholder="Método de Pagamento"
        >
          <option>Escolha a forma de pagamento</option>
          <option value="Dinheiro">Dinheiro</option>
          <option value="Cartão de crédito">Cartão de crédito</option>
          <option value="Cartão de débito">Cartão de débito</option>
        </select>
        <select
          type="text"
          name="tag"
          onChange={ this.handleForm }
          value={ tag }
          placeholder="Tipo de Despesa"
          data-testid="tag-input"
        >
          <option>Escolha o tipo de despesa</option>
          {
            arrayTipos.map((tipo, index) => (
              <option key={ index } value={ tipo }>{tipo}</option>
            ))
          }
        </select>
        <label htmlFor="coin-select">
          Moeda:
          <select
            data-testid="currency-input"
            id="coin-select"
            name="currency"
            value={ currency }
            onChange={ this.handleForm }
            label="Moeda"
          >
            {
              currencies.map((item, index) => (
                <option key={ index } value={ item }>{item}</option>
              ))
            }
          </select>
        </label>
        <button
          type="button"
          onClick={ this.saveEditInfo }
        >
          Editar despesa
        </button>
      </form>
    );
  }
}

EditExpen.propTypes = {
  currencies: PropTypes.string,
}.isRequired;

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
  idEstado: state.wallet.id,
  wallet: state.wallet,
});

const mapDispatchToProps = (dispatch) => ({
  walletExpenses: (state) => dispatch(actionExpense(state)),
  currenciesGlobal: () => dispatch(actionFetchCurrencies()),
  isEdit: (isedit) => dispatch(actionIsEdit(isedit)),
  deleteExpense: (state) => dispatch(actionDeleteExpense(state)),
});

export default connect(mapStateToProps, mapDispatchToProps)(EditExpen);
