import React from 'react';
// import { connect } from 'react-redux';
import '../css/login.css';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isDisabled: true,
      senha: '',
      textEmail: '',
    };
  }

  handleForm = ({ target }) => {
    const { name } = target;
    const { value } = target.value;
    this.setState = ({ textEmail: target.value });
  };

  render() {
    const { isDisabled, textEmail, senha } = this.state;
    return (
      <div>
        <form className="userContainer">
          <h3>Login</h3>
          <li>
            <input
              name="textEmail"
              type="email"
              placeholder="Email"
              data-testid="email-input"
              onChange={ (event) => this.setState({textEmail}) }
            />
          </li>
          <li>
            <input
              type="password"
              placeholder="Senha"
              onChange={ this.handleForm }
              data-testid="password-input"
            />
          </li>
          <button
            type="submit"
            disabled={ isDisabled }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}

