import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Header from '../components/Header';
// import { Link } from 'react-router-dom';
import fetchCurrency from '../services/Api';
import { actionWallet } from '../actions';

class Wallet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      currenciesLocal: [],
    };
  }

  componentDidMount = async () => {
    this.getCurrencyFromApi();
  }

  getCurrencyFromApi = async () => {
    this.setState({ isLoading: true });
    const { currenciesGlobal } = this.props;
    const { currenciesLocal } = this.state;
    const moedas = await fetchCurrency();
    this.setState({ currenciesLocal: moedas });
    currenciesGlobal(currenciesLocal);
    this.setState({ isLoading: false });
  }

