import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Header from '../components/Header';
import FormExpenses from '../components/FormExpenses';
import { actionFetchCurrencies } from '../actions';
// import FormResults from '../components/FormResults';

class Wallet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
    };
  }

  componentDidMount = async () => {
    this.getCurrencyFromApi();
  }

  getCurrencyFromApi = async () => {
    this.setState({ isLoading: true });
    const { currenciesGlobal } = this.props;
    await currenciesGlobal();
    this.setState({ isLoading: false });
  }

  changeValue = (value) => value.toFixed(2);

  render() {
    const { isLoading } = this.state;
    const { walletExpenses } = this.props;
    console.log(this.changeValue(10));
    return (
      <div>
        <Header />
        { isLoading ? <p>Loading</p>
          : (
            <div>
              <FormExpenses />
            </div>
          )}
        <hr />
        <h3>Despesas</h3>
        <table className="formResult">
          <thead>
            <tr>
              <th>Descrição</th>
              <th>Tag</th>
              <th>Método de pagamento</th>
              <th>Valor</th>
              <th>Moeda</th>
              <th>Câmbio utilizado</th>
              <th>Valor convertido</th>
              <th>Moeda de conversão</th>
              <th>Editar/Excluir</th>
            </tr>
          </thead>
          {
            walletExpenses.map((item, index) => (
              <tbody key={ index }>
                <tr>
                  <td>{ item.description }</td>
                  <td>{ item.tag }</td>
                  <td>{ item.method }</td>
                  <td>{ this.changeValue(item.value) }</td>
                  <td>
                    {
                      item.exchangeRates[`${item.currency}`]
                        .name.split('/Real Brasileiro')
                    }
                  </td>
                  <td>{ item.exchangeRates[`${item.currency}`].ask}</td>
                  <td>{ item.value * item.exchangeRates[`${item.currency}`].ask }</td>
                  <td>REAL</td>
                </tr>
              </tbody>
            ))
          }
        </table>
      </div>
    );
  }
}

Wallet.propTypes = {
  currenciesGlobal: PropTypes.func,
}.isRequired;

const mapDispatchToProps = (dispatch) => ({
  currenciesGlobal: () => dispatch(actionFetchCurrencies()),
});

const mapStateToProps = (state) => ({
  wallet: state.wallet,
  walletExpenses: state.wallet.expenses,
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
