import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { actionExpense, actionFetchCurrencies } from '../actions';
import fetchCurrency from '../services/Api';

class NewTable extends React.Component {
  constructor(props) {
    super(props);
    const NUMBER = 1000;
    this.state = {
      id: Math.floor((Math.random() * NUMBER) + 1),
      value: '',
      description: '',
      currency: 'USD',
      method: '',
      tag: '',
      // data: '',
    };
  }

  componentDidMount = async () => {
    await this.refreshRates();
  }

  handleForm = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  refreshRates = async () => {
    const rates = await fetchCurrency();
    this.setState({ exchangeRates: rates });
  }

  saveInfo = () => {
    this.refreshRates();
    const NUMBER = 1000;
    const { walletExpenses } = this.props;
    this.setState({ id: Math.floor((Math.random() * NUMBER) + 1) });
    walletExpenses(this.state);
    this.setState({
      value: 0,
      currency: 'USD',
      method: '',
      tag: '',
      description: '',
    });
  }

  changeValue = (value) => value.toFixed(2);

  render() {
    const { currencies } = this.props;
    const { currency, description, value, method, tag } = this.state;
    const arrayTipos = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    // className="formExpensesContainer"
    return (
      <table className="new">
        <thead>
          <tr>
            <th>Valor da despesa</th>
            <th>Descrição</th>
            <th>Forma de pagamento</th>
            <th>Tipo de despesa</th>
            <th>Moeda</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>
              <input
                type="currency"
                name="value"
                data-testid="value-input"
                onChange={ this.handleForm }
                value={ value }
                placeholder="Valor da despesa"
                min="0.00"
                max="10000000.00"
                step="0.01"
              />
            </td>
            <td>
              <input
                type="text"
                name="description"
                data-testid="description-input"
                onChange={ this.handleForm }
                value={ description }
                placeholder="Descrição da despesa"
              />
            </td>
            <td>
              <select
                type="text"
                name="method"
                data-testid="method-input"
                onChange={ this.handleForm }
                value={ method }
                placeholder="Método de Pagamento"
              >
                <option>Escolha a forma de pagamento</option>
                <option value="Dinheiro">Dinheiro</option>
                <option value="Cartão de crédito">Cartão de crédito</option>
                <option value="Cartão de débito">Cartão de débito</option>
              </select>
            </td>
            <select
              type="text"
              name="tag"
              onChange={ this.handleForm }
              value={ tag }
              placeholder="Tipo de Despesa"
              data-testid="tag-input"
            >
              <option>Escolha o tipo de despesa</option>
              {
                arrayTipos.map((tipo, index) => (
                  <option key={ index } value={ tipo }>{tipo}</option>
                ))
              }
            </select>
            <label htmlFor="coin-select">
              Moeda:
              <select
                data-testid="currency-input"
                id="coin-select"
                name="currency"
                value={ currency }
                onChange={ this.handleForm }
                label="Moeda"
              >
                {
                  currencies.map((item, index) => (
                    <option key={ index } value={ item }>{item}</option>
                  ))
                }
              </select>
            </label>
            <button
              type="button"
              onClick={ this.saveInfo }
            >
              Adicionar despesa
            </button>
          </tr>
        </tbody>
      </table>
    );
  }
}

NewTable.propTypes = {
  currencies: PropTypes.string,
}.isRequired;

const mapStateToProps = (state) => ({
  wallet: state.wallet,
  currencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  walletExpenses: (state) => dispatch(actionExpense(state)),
  currenciesGlobal: () => dispatch(actionFetchCurrencies()),
});

export default connect(mapStateToProps, mapDispatchToProps)(NewTable);
