import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Header from '../components/Header';
import FormExpenses from '../components/FormExpenses';
import { actionDeleteExpense, actionFetchCurrencies,
  actionEditExpense } from '../actions';
import EditExpen from '../components/EditExpen';
// import NewTable from '../components/NewTable';

class Wallet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  componentDidMount = async () => {
    this.getCurrencyFromApi();
  }

  getCurrencyFromApi = async () => {
    const { currenciesGlobal } = this.props;
    await currenciesGlobal();
  }

  delExpense = ({ target }) => {
    const { walletExpenses, deleteExpense } = this.props;
    const expenses = walletExpenses.filter((item) => item.id !== Number(target.value));
    deleteExpense(expenses);
  }

  editExpense = ({ target }) => {
    const { walletExpenses, editActionExpense } = this.props;
    const editExpense = walletExpenses.filter((item) => item.id === Number(target.value));
    editActionExpense(editExpense[0], true);
  }

  render() {
    const { walletExpenses, wallet: { isEdit } } = this.props;
    return (
      <div>
        <Header />
        <div>
          <FormExpenses />
        </div>
        <hr />
        <h3>Despesas</h3>
        <table className="formResult">
          <thead>
            <tr>
              <th>Descrição</th>
              <th>Tag</th>
              <th>Método de pagamento</th>
              <th>Valor</th>
              <th>Moeda</th>
              <th>Câmbio utilizado</th>
              <th>Valor convertido</th>
              <th>Moeda de conversão</th>
              <th>Editar/Excluir</th>
            </tr>
          </thead>
          <tbody>
            {
              (walletExpenses.map((item) => (
                <tr id={ item.id } key={ item.id }>
                  <td>{ item.description }</td>
                  <td>{ item.tag }</td>
                  <td>{ item.method }</td>
                  <td>{ (item.value * 1.00).toFixed(2) }</td>
                  <td>
                    {
                      item.exchangeRates[`${item.currency}`]
                        .name.split('/Real Brasileiro')
                    }
                  </td>
                  <td>
                    {
                      (item.exchangeRates[`${item.currency}`].ask * 1.00).toFixed(2)
                    }
                  </td>
                  <td>
                    {
                      (item.value * item.exchangeRates[`${item.currency}`]
                        .ask).toFixed(2)
                    }
                  </td>
                  <td>Real</td>
                  <td className="formBtnContainer">
                    <button
                      value={ item.id }
                      type="button"
                      onClick={ this.editExpense }
                      data-testid="edit-btn"
                    >
                      Edit
                    </button>
                    <button
                      value={ item.id }
                      type="button"
                      data-testid="delete-btn"
                      onClick={ this.delExpense }
                    >
                      Del
                    </button>
                  </td>
                </tr>
              ))
              )
            }
          </tbody>
        </table>
      </div>
    );
  }
}

Wallet.propTypes = {
  currenciesGlobal: PropTypes.func,
}.isRequired;

const mapDispatchToProps = (dispatch) => ({
  deleteExpense: (state) => dispatch(actionDeleteExpense(state)),
  currenciesGlobal: () => dispatch(actionFetchCurrencies()),
  editActionExpense: (state, is) => dispatch(actionEditExpense(state, is)),
});

const mapStateToProps = (state) => ({
  wallet: state.wallet,
  walletExpenses: state.wallet.expenses,
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
