import React from 'react';
import { connect } from 'react-redux';
import '../css/login.css';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isDisabled: true,
      senha: '',
      textEmail: '',
    };

    const handleForm = ({ target }) => {
      this.setState = ({
        textEmail: target.value,
      });
    };
  }

  render() {
    const { isDisabled } = this.state;
    return (
      <div>
        <form className="userContainer">
          <h3>Login</h3>
          <li>
            <input
              type="email"
              placeholder="Email"
              data-testid="email-input"
              onChange={ handleForm }
            />
          </li>
          <li>
            <input
              type="password"
              placeholder="Senha"
              data-testid="password-input"
            />
          </li>
          <button
            type="submit"
            disabled={ isDisabled }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  userEmail: state.reducer.userEmail,
});

export default Login;
